// eslint-disable-next-line no-unused-vars
import XfyunService from '@/services/xfyun';
import SparkService from '@/services/sparkAI';
import WeatherService from '@/services/weather';

// 创建SparkService实例
const sparkService = new SparkService();
const weatherService = new WeatherService();

export default {
  namespaced: true,
  state: {
    messages: [],
    isTyping: false,
    currentEmotion: 'happy',
    weatherInfo: null,
    soundEnabled: true,
    typingSound: null,
    messageSound: null
  },
  getters: {
    messages: state => state.messages,
    currentEmotion: state => state.currentEmotion
  },
  mutations: {
    addMessage(state, message) {
      state.messages.push(message);
    },
    setTyping(state, isTyping) {
      state.isTyping = isTyping;
    },
    changeEmotion(state, emotion) {
      state.currentEmotion = emotion;
    },
    setWeatherInfo(state, info) {
      state.weatherInfo = info;
    },
    toggleSoundEffects(state) {
      state.soundEnabled = !state.soundEnabled;
    },
    clearMessages(state) {
      state.messages = [];
    }
  },
  actions: {
    async sendMessage({ commit, state, dispatch }, text) {
      // 确保text是字符串
      if (!text || typeof text !== 'string' || !text.trim()) return;

      // 添加用户消息
      const userMessage = {
        text,
        isUser: true,
        timestamp: new Date()
      };
      commit('addMessage', userMessage);

      // 设置正在输入状态
      commit('setTyping', true);

      // 分析情绪
      dispatch('analyzeEmotion', text);

      // 检查是否是天气相关问题
      const isWeatherQuery = /天气|温度|下雨|下雪|冷|热|气温|阴天|晴天|多云/i.test(text);
      if (isWeatherQuery) {
        await dispatch('checkWeather');
      }

      try {
        console.log('尝试使用星火大模型生成回复...');
        // 默认使用本地回退逻辑生成一个回复
        let response = generateResponse(text, state.currentEmotion);

        // 尝试使用星火大模型API生成回复
        try {
          // 如果是关于天气的问题并且已经获取了天气信息，将天气信息添加到请求中
          let promptText = text;

          if (isWeatherQuery && state.weatherInfo) {
            promptText = `${text}\n\n以下是实时天气信息，请根据它回答用户：\n城市：${state.weatherInfo.city}\n温度：${state.weatherInfo.temp}°C\n天气状况：${state.weatherInfo.condition}\n描述：${state.weatherInfo.description}`;
          }

          const aiResponse = await sparkService.generateResponse(promptText, state.currentEmotion);
          // 如果成功获取AI回复，则使用AI回复
          if (aiResponse && aiResponse.length > 0) {
            response = aiResponse;
            console.log('成功获取AI回复');
          }
        } catch (aiError) {
          console.error('使用AI生成回复失败，使用本地回退:', aiError);
          // 使用本地生成的回复，已在上面生成
        }

        // 添加助手消息
        const assistantMessage = {
          text: response,
          isUser: false,
          timestamp: new Date()
        };
        commit('addMessage', assistantMessage);
      } catch (error) {
        console.error('获取回复失败:', error);
        // 错误发生时使用一个简单的回退回复
        const assistantMessage = {
          text: '糖球遇到一点小问题，请稍后再试~ 😅',
          isUser: false,
          timestamp: new Date()
        };
        commit('addMessage', assistantMessage);
      } finally {
        // 无论成功失败，都关闭输入状态
        commit('setTyping', false);
      }
    },

    analyzeEmotion({ commit }, text) {
      if (typeof text !== 'string') return;

      const emotion = analyzeEmotion(text);
      commit('changeEmotion', emotion);
    },

    async checkWeather({ commit }) {
      try {
        // 使用和风天气API获取天气数据
        const weatherResponse = await weatherService.getNowWeather();
        if (weatherResponse.success) {
          commit('setWeatherInfo', weatherResponse);
        } else {
          // 如果API调用失败，使用模拟数据
          const weatherInfo = {
            city: '北京',
            temp: Math.round(10 + Math.random() * 20),
            condition: ['晴朗', '多云', '阴天', '小雨'][Math.floor(Math.random() * 4)],
            description: '今天天气不错，适合出门玩耍~'
          };
          commit('setWeatherInfo', weatherInfo);
        }
      } catch (error) {
        console.error('获取天气信息失败', error);
        // 使用模拟数据作为备用
        const weatherInfo = {
          city: '北京',
          temp: Math.round(10 + Math.random() * 20),
          condition: ['晴朗', '多云', '阴天', '小雨'][Math.floor(Math.random() * 4)],
          description: '无法获取实时天气数据，显示模拟天气~'
        };
        commit('setWeatherInfo', weatherInfo);
      }
    },

    clearMessages({ commit }) {
      commit('clearMessages');
    }
  }
};

// 辅助函数
function analyzeEmotion(text) {
  if (typeof text !== 'string') return 'neutral';

  // 简单的关键词匹配来确定情绪
  if (/开心|喜欢|棒|好|爱|感谢|谢谢|高兴/i.test(text)) {
    return 'happy';
  } else if (/难过|伤心|痛苦|悲|哭|嘤/i.test(text)) {
    return 'sad';
  } else if (/生气|烦|讨厌|恨|滚|笨蛋|蠢/i.test(text)) {
    return 'angry';
  } else if (/惊讶|哇|天啊|不会吧|不可能|真的|竟然/i.test(text)) {
    return 'surprised';
  } else if (/困惑|为什么|怎么|不懂|不理解|疑惑|问题/i.test(text)) {
    return 'confused';
  } else if (/困|累|疲倦|好困|好累|睡觉/i.test(text)) {
    return 'tired';
  } else if (/激动|兴奋|期待|等不及/i.test(text)) {
    return 'excited';
  }
  return 'neutral';
}

// 本地回退函数，当API调用失败时使用
function generateResponse(text, emotion) {
  if (typeof text !== 'string') return '糖球不太明白你的意思呢~ 😅';

  // 根据用户输入和当前情绪生成回复
  const greetings = ['你好', '嗨', '早上好', '晚上好', '你好呀'];
  if (greetings.some(g => text.includes(g))) {
    return `嗨嗨！我是糖球呀~ 有什么我能帮到你的吗？ 😊`;
  }

  if (text.includes('介绍') && text.includes('甜梦星球')) {
    return '甜梦星球是一个充满可爱角色的梦幻世界~我是这里的居民糖球，很高兴认识你！这里有博客、小游戏和许多好玩的东西呢 💕';
  }

  if (text.includes('天气')) {
    return '我正在查看窗外的天气呢~稍等一下，马上告诉你！🌈';
  }

  if (text.includes('游戏')) {
    return '想玩游戏吗？我们有记忆配对和云朵农场哦！点击导航栏的"游戏"就能找到啦~ 🎮';
  }

  const emotions = {
    happy: [
      '看到你这么开心，我也跟着高兴起来了！😊',
      '嘿嘿，糖球也很开心呢~一起来分享快乐吧！💫',
      '开心的日子真美好，希望你每天都这么快乐！🌟'
    ],
    sad: [
      '别难过啦，糖球给你一个柔软的抱抱~ 🤗',
      '我变成一个毛绒抱枕陪着你，伤心的时候依靠我吧 💗',
      '糖球不喜欢看到你难过，来听听轻松的音乐吧~ 🎵'
    ],
    angry: [
      '深呼吸~生气对身体不好，糖球陪你冷静一下 😌',
      '我变成一个解压球，来捏捏我消消气吧！💗',
      '糖球在这里，有什么委屈可以告诉我哦~ 🌸'
    ],
    confused: [
      '看起来你有些疑惑，需要我解释清楚吗？🤔',
      '问题有时候确实很复杂，让我们一起想想办法吧！💭',
      '糖球虽然不是全知全能，但会尽力帮你解答疑惑~ 📚'
    ]
  };

  const defaultResponses = [
    '糖球在听呢！请继续说吧~ 😊',
    '嗯嗯，我明白你的意思！有什么需要帮忙的吗？💫',
    '你说的真有趣！糖球很喜欢和你聊天呢~ 💕',
    '今天过得怎么样呀？有什么开心的事情吗？🌟',
    '糖球可以陪你聊天、查天气、玩游戏，需要我做什么呢？~ 🎀'
  ];

  if (emotions[emotion]) {
    const responses = emotions[emotion];
    return responses[Math.floor(Math.random() * responses.length)];
  }

  return defaultResponses[Math.floor(Math.random() * defaultResponses.length)];
}
