  async fetchWeatherData(url) {
    const response = await fetch(url, {
      headers: {
        'Accept': 'application/json',
        'Accept-Encoding': 'gzip'
      }
    });

    if (!response.ok) {
      throw new Error(`Â§©Ê∞îAPIËØ∑Ê±ÇÂ§±Ë¥•: ${response.status}`);
    }

    const data = await response.json();

    if (data.code === '200') {
      const { temp, text, icon } = data.now;
      const city = this.defaultCities.find(c => c.id === data.location?.id);
      return {
        temperature: temp,
        condition: this.getWeatherCondition(icon),
        description: text,
        cityName: city ? city.name : data.location?.name,
        updateTime: data.updateTime
      };
    } else {
      throw new Error(`Â§©Ê∞îAPIËøîÂõûÈîôËØØ: ${data.code}`);
    }
  }

