import { getToken, setToken, removeToken } from '@/utils/auth'
import { login, logout, getInfo, register, updateProfile } from '@/api/user'

// 初始状态
const state = {
  token: getToken(),
  user: null,
  loading: false,
  error: null
}

// getters
const getters = {
  isLoggedIn: state => !!state.token,
  currentUser: state => state.user,
  isLoading: state => state.loading,
  authError: state => state.error
}

// mutations
const mutations = {
  SET_TOKEN(state, token) {
    state.token = token
  },
  SET_USER(state, user) {
    state.user = user
  },
  SET_LOADING(state, loading) {
    state.loading = loading
  },
  SET_ERROR(state, error) {
    state.error = error
  },
  RESET_STATE(state) {
    state.token = ''
    state.user = null
    state.error = null
    removeToken()
  }
}

// actions
const actions = {
  // 用户登录
  async login({ commit }, { email, password, remember }) {
    try {
      commit('SET_LOADING', true)
      commit('SET_ERROR', null)

      const response = await login({ email: email.trim(), password })
      const { data } = response.data

      if (data.token) {
        commit('SET_TOKEN', data.token)
        // 只有在用户选择"记住我"时才保存token到localStorage
        if (remember) {
          setToken(data.token)
        }

        // 获取用户信息
        const userResponse = await getInfo()
        const userData = userResponse.data.data
        commit('SET_USER', userData)
      }

      return data
    } catch (error) {
      const message = error.response?.data?.message || error.message || '登录失败'
      commit('SET_ERROR', message)
      throw new Error(message)
    } finally {
      commit('SET_LOADING', false)
    }
  },

  // 用户注册
  async register({ commit }, { username, email, password }) {
    try {
      commit('SET_LOADING', true)
      commit('SET_ERROR', null)

      const { data } = await register({ username, email, password })

      commit('SET_TOKEN', data.token)
      setToken(data.token)

      // 获取用户信息
      const userInfo = await getInfo(data.token)
      commit('SET_USER', userInfo.data)

      return data
    } catch (error) {
      commit('SET_ERROR', error.message || '注册失败')
      throw error
    } finally {
      commit('SET_LOADING', false)
    }
  },

  // 获取用户信息
  async getInfo({ commit, state }) {
    try {
      const { data } = await getInfo(state.token)
      commit('SET_USER', data)
      return data
    } catch (error) {
      commit('SET_ERROR', error.message)
      throw error
    }
  },

  // 更新用户资料
  async updateProfile({ commit }, profileData) {
    try {
      commit('SET_LOADING', true)
      const { data } = await updateProfile(profileData)
      commit('SET_USER', data)
      return data
    } catch (error) {
      commit('SET_ERROR', error.message)
      throw error
    } finally {
      commit('SET_LOADING', false)
    }
  },

  // 用户登出
  async logout({ commit }) {
    try {
      await logout()
      removeToken()
      commit('RESET_STATE')
    } catch (error) {
      console.error('登出失败:', error)
      // 即使请求失败，也清除本地状态
      removeToken()
      commit('RESET_STATE')
      throw error
    }
  },

  // 重置 Token
  resetToken({ commit }) {
    removeToken()
    commit('RESET_STATE')
  }
}

export default {
  namespaced: true,
  state,
  getters,
  actions,
  mutations
}
