        console.log('Ëß£ÊûêÂà∞ÁöÑÂ§©Ê∞îÊï∞ÊçÆ:', temp, text, icon);
        const city = this.defaultCities.find(c => c.id === cityId) || { name: 'Êú™Áü•ÂüéÂ∏Ç' };
        return {
          temperature: temp,
          condition: this.getWeatherCondition(icon),
          description: text,
          cityName: city.name,
          updateTime: weatherData.updateTime
        };
      } else {
        console.error('Ëé∑ÂèñÂ§©Ê∞îÂ§±Ë¥•, ÈîôËØØÁ†Å:', weatherData.code);
        return this.getDefaultWeather();
      }
    } catch (error) {
      console.error('ÂàáÊç¢ÂüéÂ∏ÇÂá∫Èîô:', error);
      return this.getDefaultWeather();
    }
  }

  // Ëß£ÊûêÂüéÂ∏ÇÂêçÁß∞
