import XfyunService from '@/services/xfyun';
import SparkService from '@/services/sparkAI';

const sparkService = new SparkService();

export default {
  namespaced: true,
  state: {
    messages: [],
    isTyping: false,
    currentEmotion: 'happy',
    weatherInfo: null,
    soundEnabled: true,
    typingSound: null,
    messageSound: null
  },
  getters: {
    messages: state => state.messages,
    currentEmotion: state => state.currentEmotion
  },
  mutations: {
    addMessage(state, message) {
      state.messages.push(message);
    },
    setTyping(state, isTyping) {
      state.isTyping = isTyping;
    },
    changeEmotion(state, emotion) {
      state.currentEmotion = emotion;
    },
    setWeatherInfo(state, info) {
      state.weatherInfo = info;
    },
    toggleSoundEffects(state) {
      state.soundEnabled = !state.soundEnabled;
    },
    clearMessages(state) {
      state.messages = [];
    }
  },
  actions: {
    async sendMessage({ commit, state, dispatch }, text) {
      // 确保text是字符串
      if (!text || typeof text !== 'string' || !text.trim()) return;

      // 添加用户消息
      const userMessage = {
        text,
        isUser: true,
        timestamp: new Date()
      };
      commit('addMessage', userMessage);

      // 设置正在输入状态
      commit('setTyping', true);

      // 分析情绪
      dispatch('analyzeEmotion', text);

      // 检查是否是天气相关问题
      const isWeatherQuery = /天气|温度|下雨|下雪|冷|热|气温|阴天|晴天|多云/i.test(text);
      if (isWeatherQuery) {
        await dispatch('checkWeather');
      }

      try {
        // 使用星火大模型API生成回复
        const response = await sparkService.generateResponse(text, state.currentEmotion);

        // 添加助手消息
        const assistantMessage = {
          text: response,
          isUser: false,
          timestamp: new Date()
        };
        commit('addMessage', assistantMessage);
      } catch (error) {
        console.error('获取AI回复失败:', error);
        // 错误发生时使用本地回退逻辑
        const fallbackResponse = generateResponse(text, state.currentEmotion);

        const assistantMessage = {
          text: fallbackResponse,
          isUser: false,
          timestamp: new Date()
        };
        commit('addMessage', assistantMessage);
      } finally {
        // 无论成功失败，都关闭输入状态
        commit('setTyping', false);
      }
    },

    analyzeEmotion({ commit }, text) {
      if (typeof text !== 'string') return;

      const emotion = analyzeEmotion(text);
      commit('changeEmotion', emotion);
    },

    async checkWeather({ commit }) {
      try {
        // 模拟天气数据
        const weatherInfo = {
          city: '北京',
          temp: Math.round(10 + Math.random() * 20),
          condition: ['晴朗', '多云', '阴天', '小雨'][Math.floor(Math.random() * 4)],
          description: '今天天气不错，适合出门玩耍~'
        };
        commit('setWeatherInfo', weatherInfo);
      } catch (error) {
        console.error('获取天气信息失败', error);
      }
    },

    clearMessages({ commit }) {
      commit('clearMessages');
    }
  }
};

// 辅助函数
function analyzeEmotion(text) {
  if (typeof text !== 'string') return 'neutral';

  // 简单的关键词匹配来确定情绪
  if (/开心|喜欢|棒|好|爱|感谢|谢谢|高兴/i.test(text)) {
    return 'happy';
  } else if (/难过|伤心|痛苦|悲|哭|嘤/i.test(text)) {
    return 'sad';
  } else if (/生气|烦|讨厌|恨|滚|笨蛋|蠢/i.test(text)) {
    return 'angry';
  } else if (/惊讶|哇|天啊|不会吧|不可能|真的|竟然/i.test(text)) {
    return 'surprised';
  } else if (/困惑|为什么|怎么|不懂|不理解|疑惑|问题/i.test(text)) {
    return 'confused';
  } else if (/困|累|疲倦|好困|好累|睡觉/i.test(text)) {
    return 'tired';
  } else if (/激动|兴奋|期待|等不及/i.test(text)) {
    return 'excited';
  }
  return 'neutral';
}

// 本地回退函数，当API调用失败时使用
function generateResponse(text, emotion) {
  if (typeof text !== 'string') return '糖球不太明白你的意思呢~ (=^･ω･^)=';

  // 根据用户输入和当前情绪生成回复
  const greetings = ['你好', '嗨', '早上好', '晚上好', '你好呀'];
  if (greetings.some(g => text.includes(g))) {
    return `嗨嗨！我是糖球呀~ 有什么我能帮到你的吗？ (●ˇ∀ˇ●)`;
