class WeatherService {
  constructor() {
    // 检查环境变量
    if (typeof import.meta.env === 'undefined') {
      console.error('环境变量未正确加载');
    }

    // 使用环境变量或默认值
    this.apiKey = import.meta.env?.VITE_APP_WEATHER_KEY || '20ad4f9133c94ac69f48192ef755f473';
    this.baseUrl = 'https://devapi.qweather.com/v7';

    console.log('WeatherService initialized with:', {
      apiKey: this.apiKey?.substring(0, 10) + '...',
      baseUrl: this.baseUrl
    });
  }

  // 获取当前天气
  async getNowWeather(location = '101010100') {
    try {
      const url = `${this.baseUrl}/weather/now?location=${location}&key=${this.apiKey}`;
      const response = await fetch(url);
      const data = await response.json();

      if (data.code === '200') {
        return {
          temperature: data.now.temp,
          condition: this.getWeatherCondition(data.now.icon),
          description: data.now.text
        };
      } else {
        console.error('获取天气失败:', data);
        return this.getDefaultWeather();
      }
    } catch (error) {
      console.error('获取天气出错:', error);
      return this.getDefaultWeather();
    }
  }

  // 生成天气描述
  generateWeatherDescription(weather) {
    const { temperature, condition, description } = weather;
    let suggestion = '';

    // 根据温度给出建议
    if (temperature < 10) {
      suggestion = '天气有点冷，要注意保暖哦~ 🧥';
    } else if (temperature > 30) {
      suggestion = '天气有点热，记得防晒补水哦~ 🌞';
    } else {
      suggestion = '天气很舒适，适合出门活动呢~ 🌈';
    }

    // 根据天气状况添加特定建议
    if (condition === 'rainy') {
      suggestion += ' 记得带伞！☔';
    } else if (condition === 'sunny') {
      suggestion += ' 防晒要做好！🧴';
    }

    return `现在气温${temperature}°C，${description}。${suggestion}`;
  }

  // 获取天气状况
  getWeatherCondition(icon) {
    const conditions = {
      sunny: ['100', '101', '102', '103'],
      cloudy: ['104', '150', '151', '152', '153'],
      rainy: ['300', '301', '302', '303', '304', '305', '306', '307', '308', '309', '310', '311', '312', '313', '314', '315', '316', '317', '318', '399'],
      snowy: ['400', '401', '402', '403', '404', '405', '406', '407', '408', '409', '410', '499'],
      foggy: ['500', '501', '502', '503', '504', '507', '508', '509', '510', '511', '512', '513', '514', '515']
    };

    for (const [condition, icons] of Object.entries(conditions)) {
      if (icons.includes(icon)) {
        return condition;
      }
    }
    return 'cloudy'; // 默认返回多云
  }

  // 获取默认天气数据
  getDefaultWeather() {
    return {
      temperature: 25,
      condition: 'cloudy',
      description: '多云'
    };
  }
}

export default new WeatherService();
